mapper.select:

	select id, title, logo
	from blog
	where id = #{id}


	select no, name
	from category
	where id = #{id}

choose.when:

	select p.no, p.title, p.contents, date_format(p.reg_date,'%Y/%m/%d') as regDate , p.category_no as categoryNo 
	from post p
	join category c on(p.category_no = c.no)
	where c.id = #{id}
	and p.category_no = #{categoryNo}
	order by no desc


	select p.no, p.title, p.contents, date_format(p.reg_date,'%Y/%m/%d') regDate, 
	p.category_no as categoryNo , c.name
	from post p
	right join category c on(p.category_no = c.no)
	where c.id = #{id}
	and p.category_no = #{categoryNo}
	order by 
	p.no != #{postNo}
	asc;

mapper.select:

	select title, logo
	from blog
	where id = #{id}

choose.when:

	update blog
	set title = #{title}
	where id = #{id}

choose.otherwise:

	update blog
	set title = #{title}, logo = #{logo}
	where id = #{id}

mapper.delete:

	delete from category
	where id = #{id}
	and no = #{no}

mapper.select:

	select name
	from category
	where id = #{id}


	select no
	from category 
	where name = #{category}
	and id = #{id}
